mapper.select:
SELECT goods.id AS goods_id, goods.name AS goods_name, goods.stocked, goods_specification.id AS goods_specification_id, goods_specification.name AS goods_specification_name, goods_specification.price, package_group_detail.quantity, package_group_detail.package_id, package_group_detail.package_group_id, package_group.group_name AS package_group_name, package_group.group_type AS package_group_type FROM package_group_detail INNER JOIN package_group ON package_group.id = package_group_detail.package_group_id
AND package_group.group_type = #{groupType}
AND package_group.deleted = 0 INNER JOIN goods ON goods.id = package_group_detail.goods_id AND goods.deleted = 0 INNER JOIN goods_specification ON goods_specification.id = package_group_detail.goods_specification_id AND goods_specification.deleted = 0 WHERE package_group_detail.package_id IN
#{packageId}
AND package_group_detail.deleted = 0;

SELECT
goods.*,
goods_category.name AS category_name
FROM goods
INNER JOIN goods_category ON goods_category.id = goods.category_id AND goods_category.deleted = 0
WHERE goods.deleted = 0
AND goods.tenant_id = #{tenantId}
AND goods.branch_id = #{branchId}
AND goods.id IN

    #{goodsId}

SELECT
goods.*,
goods_category.name AS category_name
FROM goods
INNER JOIN goods_category ON goods_category.id = goods.category_id AND goods_category.deleted = 0
WHERE goods.deleted = 0
AND goods.tenant_id = #{tenantId}
AND goods.branch_id = #{branchId}
AND goods.category_id = #{categoryId}
